<script type="text/javascript">
    $(document).ready(function() {
        var CustomSelectionAdapter = $.fn.select2.amd.require("select2/selection/customSelectionAdapter");

        $('.categories').select2({
            placeholder: "Choose a Category"
        });
        $('#tags').select2({
            placeholder: "Filter by tags",
            allowClear: true,
            selectionAdapter: CustomSelectionAdapter
        });
    });

    $(document).ready(function(){
      $("#keywords").on("keyup", function() {
        var value = $(this).val().toLowerCase();
        $("#listing_data #listing_item").filter(function() {
          $(this).toggle($(this).text().toLowerCase().indexOf(value) > -1)
        });
      });
    });

    
</script>
<script type="text/javascript">
        $('#search').on('input', function(){
            var keyword = $(this).val();
            var url = "https://www.bisitailigan.com/search?keyword=" + keyword;
            var el = $('#search-autosuggest');
            el.empty();
            el.show();
            $.getJSON(url, function(data){

                if(data.length <=0 ){
                    el.append('<a href="#!" class="autosuggest-item text-danger">No results found</a>');
                }else{
                   $.each(data, function(i, item){
                       el.append('<a href="../../listing/'+item['slug']+'" class="autosuggest-item">'+item['title']+'</a>');
                   }); 
                }
                
            });
        });
    </script>


